md"""
```julia
# author of original version: Tanja Clees
# modifications and extensions by Ferdinand Rieck

using DifferentialEquations, Plots, BenchmarkTools
using PrettyTables

function dufflin!(dy,y,p,t)
	α, β, γ, δ, ω = p
	dy[1] = y[2]
	dy[2] = γ*cos(ω*t) - δ*y[2] -  β*y[1] - α*y[1]^3
end

δ = 1.0; α = 1.0; γ = 1.0; ω = 1.0; β = -1.0;   
p = α, β, γ, δ, ω 
y0 = [0.0; 0.0]
tspan = (0.0,100.0)
prob = ODEProblem(dufflin!,y0,tspan,p)

solver = (BS3(), Tsit5(), Vern7(), Rosenbrock23(), QNDF(), 
          Rodas4P(), Rodas4P2(), Rodas5P(), RadauIIA5(), 
          AutoTsit5(Rosenbrock23()))
stringSolver = ["BS3", "Tsit5", "Vern7", "Rosenbrock23", "QNDF", 
                "Rodas4P", "Rodas4P2", "Rodas5P","RadauIIA5", 
                "AutoTsit5(Rosenbrock23())"]
deltaWerte = [1.0, 10.0, 100.0, 1000.0, 2000.0, 3000.0]
stringdeltaWerte = ["1", "10", "100", "1000", "2000", "3000"]
nDelta = length(deltaWerte)
nSolver = length(solver)
zeiten = zeros(nSolver, nDelta)
error = zeros(nSolver, nDelta)
steps = zeros(nSolver, nDelta)

for i = 1:nSolver
    for j = 1:nDelta
        global p, prob_new, solver1, zeiten, error, steps
        p = (α, β, γ, deltaWerte[j], ω)
        prob_new = remake(prob; p)
        println("Lauf i=$i, j=$j ...")
        solver1 = solver[i]
        sol_zeit = @benchmark solve(prob_new, solver1, reltol=1e-4, abstol = 1e-6)
        sol = solve(prob_new, solver1, reltol=1e-4,  abstol = 1e-6)
        solExact = solve(prob_new,Tsit5(), reltol=1e-14,  abstol = 1e-14)

        zeiten[i,j] = minimum(sol_zeit.times)/1e6
        error[i,j] = maximum(abs.(sol[1,:]-solExact(sol.t)[1,:]))
        steps[i,j] = trunc(Int,sol.destats.naccept) 

        plot(sol[1,:], sol[2,:], xlabel="y", ylabel="dy", 
             label=string(stringSolver[i]," δ = ",stringdeltaWerte[j]), 
             legend = :outerbottom, dpi = 300)
        png(string("y_dy_", stringSolver[i],"_", stringdeltaWerte[j]))
        plot(sol.t,sol[1,:], xlabel="t", ylabel="y", 
             label=string(stringSolver[i]," δ = ",stringdeltaWerte[j]), 
             legend = :outerbottom, dpi = 300)
        png(string("t_y_", stringSolver[i],"_", stringdeltaWerte[j]))
    end
end

ZeitTable = hcat(stringSolver,zeiten)
ErrorTable = hcat(stringSolver,error)
StepsTable = hcat(stringSolver,steps)
headerOZeit = ["Solver"]
headerOError = ["Solver"]
headerOSteps = ["Solver"]
headerU = [""]
for i = 1:nDelta
    global headerOZeit = [headerOZeit; "Rechenzeit [ms]"]
    global headerOError = [headerOError; "max error"]
    global headerOSteps = [headerOSteps; "Zeitschritte"]
    global headerU = [headerU; string("δ = ",stringdeltaWerte[i])]
end
header = (headerOZeit, headerU)
pretty_table(ZeitTable, header=header)
header = (headerOError, headerU)
pretty_table(ErrorTable, header=header)
header = (headerOSteps, headerU)
pretty_table(StepsTable, header=header)


```
