	task(text) = Markdown.MD(Markdown.Admonition("danger", "", [text]));

	hinweis(text) = Markdown.MD(Markdown.Admonition("correct", "Hinweis:", [text]));

	html"""<a class="anchor" id="ende"></a>"""
end

